let counter = 5;
body.addEventListener('keydown', (el) => {
  
  if (counter < 0) {
    alert("Your pet ran away!")
    arrOfDivs[0].removeChild(pet);
  }
  else if (el.code === 'ArrowDown') {
    arrOfDivs[counter].appendChild(pet);
    counter++;
  }
  else if (el.code === 'ArrowUp') {
    arrOfDivs[counter].appendChild(pet);
    counter--;
  }
})

