const body = document.querySelector('body');
let city = prompt('What city do you want? Sydney or NYC?');
// Declares array of divs 
const arrOfDivs = document.querySelectorAll('div');
// Declare the pet
const pet = document.createElement('img')


let completeData = null;
// New York City -> huge rat
  // IF they choose NYC, assign image to rat
  // call fetch on NYC weather api 
if (city === 'NYC') {
  // Pet to rat
  pet.src = 'data:image/png;base64,';
  // ASYNC Web API Func
  const getData = async () => {
    const res = await fetch(
        'https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/new%20york%20city?unitGroup=metric&include=hours&key=35QLXFH2J8ME5YC6EEDY5SCUP&contentType=json'
    );
    const data = await res.json();
    return data;
  };
  getData().then(ourData => {
    const currentState = ourData['days'][0]['icon']
    console.log(currentState)
    let image = null;
    if (currentState === 'rain' || currentState === 'snow' || currentState === 'fog' || currentState === 'cloudy' || currentState === 'partly-cloudy-day') image = 'data:image/jpeg;base64,';
    if (currentState === 'wind' || currentState === 'clear-day') image = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXYsf22eUSX7NRR4IZW8FqIr_zSRyK_zuZCQ&usqp=CAU';
    if (currentState === 'clear-night' || currentState === 'partly-cloudy-night') image = 'https://www.nicepng.com/png/detail/768-7681706_cresent-moon-cartoon-images-clipart-best-yellow-moon.png';
    const weather = document.createElement('img');
    weather.src = image;
    weather.style.width = '100px';
    weather.style.height = 'auto';
    body.prepend(weather);
  });
}



// Sydney, Australia -> Kangaroo
  // IF they choose Sydney, assign image to kangaroo
  // call fetch on Sydney weather 
