    if (currentState === 'rain' || currentState === 'snow' || currentState === 'fog' || currentState === 'cloudy' || currentState === 'partly-cloudy-day') image = 'data:image/jpeg;base64,';
    if (currentState === 'wind' || currentState === 'clear-day') image = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXYsf22eUSX7NRR4IZW8FqIr_zSRyK_zuZCQ&usqp=CAU';
    if (currentState === 'clear-night' || currentState === 'partly-cloudy-night') image = 'https://www.nicepng.com/png/detail/768-7681706_cresent-moon-cartoon-images-clipart-best-yellow-moon.png';
